class LessonNavigationManager{constructor(){this.currentLessonId=null;this.packageId=null;this.lessonData=null;this.navigationData=null;this.init();}init(){this.extractLessonInfo();this.bindNavigationEvents();this.loadNavigationData();this.setupKeyboardNavigation();this.checkPrerequisites();}extractLessonInfo(){const markCompleteBtn=document.getElementById('markCompleteBtn');if(markCompleteBtn){this.currentLessonId=markCompleteBtn.dataset.lessonId;}const packageLink=document.querySelector('.breadcrumb-item[href*="/content/package/"]');if(packageLink){const match=packageLink.href.match(/\/content\/package\/(\d+)\/lessons/);if(match){this.packageId=match[1];}}}bindNavigationEvents(){const prevBtn=document.querySelector('.nav-btn.nav-prev');const nextBtn=document.querySelector('.nav-btn.nav-next');if(prevBtn){prevBtn.addEventListener('click',(e)=>{e.preventDefault();this.navigateToPrevious();});}if(nextBtn){nextBtn.addEventListener('click',(e)=>{e.preventDefault();this.navigateToNext();});}this.bindQuickNavigationEvents();this.bindAutoNavigationEvents();}setupKeyboardNavigation(){document.addEventListener('keydown',(e)=>{if(e.target.tagName==='INPUT'||e.target.tagName==='TEXTAREA'){return;}switch(e.key){case 'ArrowLeft':e.preventDefault();this.navigateToPrevious();break;case 'ArrowRight':e.preventDefault();this.navigateToNext();break;case ' ':e.preventDefault();this.triggerMarkComplete();break;case 'Escape':this.closeNavigationModals();break;}});}async loadNavigationData(){try{if(!this.packageId)return;const response=await fetch(`/content/api/package/${this.packageId}/lessons`);const data=await response.json();if(data.success){this.navigationData=data.data;this.updateNavigationUI();}}catch(error){console.error('Erro ao carregar dados de navega√ß√£o:',error);}}async checkPrerequisites(){try{if(!this.currentLessonId)return;const response=await fetch(`/content/api/lesson/${this.currentLessonId}/prerequisites`);const data=await response.json();if(data.success&&!data.data.can_access){this.showPrerequisiteWarning(data.data.missing_prerequisites);}}catch(error){console.error('Erro ao verificar pr√©-requisitos:',error);}}async navigateToPrevious(){try{const prevBtn=document.querySelector('.nav-btn.nav-prev');if(!prevBtn||prevBtn.classList.contains('disabled')){this.showNotification('Esta √© a primeira aula do pacote','info');return;}this.showNavigationLoading('prev');window.location.href=`/content/lesson/${this.currentLessonId}/previous`;}catch(error){console.error('Erro na navega√ß√£o anterior:',error);this.showNotification('Erro ao navegar para aula anterior','error');this.hideNavigationLoading();}}async navigateToNext(){try{const nextBtn=document.querySelector('.nav-btn.nav-next');if(!nextBtn||nextBtn.classList.contains('disabled')){this.showNotification('Esta √© a √∫ltima aula do pacote','info');return;}const canProceed=await this.checkCanProceedToNext();if(!canProceed){this.showCompletionPrompt();return;}this.showNavigationLoading('next');window.location.href=`/content/lesson/${this.currentLessonId}/next`;}catch(error){console.error('Erro na navega√ß√£o pr√≥xima:',error);this.showNotification('Erro ao navegar para pr√≥xima aula','error');this.hideNavigationLoading();}}async checkCanProceedToNext(){try{const response=await fetch(`/content/api/lesson/${this.currentLessonId}/status`);const data=await response.json();if(data.success){return data.data.is_completed;}return !1;}catch(error){console.error('Erro ao verificar status da aula:',error);return !1;}}showCompletionPrompt(){const modal=this.createModal({title:'Concluir Aula',message:'Voc√™ precisa marcar esta aula como conclu√≠da antes de prosseguir para a pr√≥xima.',buttons:[{text:'Marcar como Conclu√≠da',class:'btn-primary',action:()=>{this.triggerMarkComplete();this.closeModal(modal);}},{text:'Cancelar',class:'btn-secondary',action:()=>this.closeModal(modal)}]});document.body.appendChild(modal);setTimeout(()=>modal.classList.add('show'),10);}bindQuickNavigationEvents(){const quickNavBtn=document.getElementById('quickNavBtn');if(quickNavBtn){quickNavBtn.addEventListener('click',()=>{this.toggleQuickNavigation();});}const lessonLinks=document.querySelectorAll('.lesson-list-item');lessonLinks.forEach(link=>{link.addEventListener('click',(e)=>{e.preventDefault();this.navigateToLesson(link.dataset.lessonId);});});}bindAutoNavigationEvents(){document.addEventListener('lessonCompleted',(e)=>{if(e.detail.nextLesson){this.showAutoNavigationPrompt(e.detail.nextLesson);}});}showAutoNavigationPrompt(nextLesson){const modal=this.createModal({title:'Parab√©ns!üéâ',message:`Aula conclu√≠da com sucesso!Deseja continuar para a pr√≥xima aula:"${nextLesson.name}"?`,buttons:[{text:'Continuar',class:'btn-primary',action:()=>{window.location.href=nextLesson.url;}},{text:'Ficar Aqui',class:'btn-secondary',action:()=>this.closeModal(modal)}],autoClose:10000});document.body.appendChild(modal);setTimeout(()=>modal.classList.add('show'),10);this.startAutoNavigationCountdown(modal,nextLesson.url,10);}startAutoNavigationCountdown(modal,url,seconds){const countdownEl=modal.querySelector('.countdown');if(!countdownEl)return;let remaining=seconds;const interval=setInterval(()=>{remaining--;countdownEl.textContent=remaining;if(remaining<=0){clearInterval(interval);window.location.href=url;}},1000);modal.addEventListener('click',()=>{clearInterval(interval);});}async navigateToLesson(lessonId){try{const response=await fetch(`/content/api/lesson/${lessonId}/prerequisites`);const data=await response.json();if(data.success&&!data.data.can_access){this.showPrerequisiteWarning(data.data.missing_prerequisites);return;}window.location.href=`/content/lesson/${lessonId}`;}catch(error){console.error('Erro ao navegar para aula:',error);this.showNotification('Erro ao navegar para aula','error');}}showPrerequisiteWarning(missingPrerequisites){const prereqList=missingPrerequisites.map(p=>`‚Ä¢ ${p.name}`).join('\n');const modal=this.createModal({title:'Pr√©-requisitos Necess√°rios',message:`Para acessar esta aula,voc√™ precisa completar:\n\n${prereqList}`,buttons:[{text:'Entendi',class:'btn-primary',action:()=>this.closeModal(modal)}]});document.body.appendChild(modal);setTimeout(()=>modal.classList.add('show'),10);}updateNavigationUI(){if(!this.navigationData)return;this.updatePackageProgress();this.updateLessonList();this.updateNavigationIndicators();}updatePackageProgress(){const progressBar=document.querySelector('.progress-fill');const progressText=document.querySelector('.progress-percentage');if(progressBar&&this.navigationData.progressStats){const percentage=this.navigationData.progressStats.progress_percentage;progressBar.style.width=`${percentage}%`;if(progressText){progressText.textContent=`${percentage}%`;}}}showNavigationLoading(direction){const btn=document.querySelector(`.nav-btn.nav-${direction}`);if(btn){btn.classList.add('loading');btn.style.pointerEvents='none';}}hideNavigationLoading(){const btns=document.querySelectorAll('.nav-btn.loading');btns.forEach(btn=>{btn.classList.remove('loading');btn.style.pointerEvents='auto';});}triggerMarkComplete(){const markCompleteBtn=document.getElementById('markCompleteBtn');if(markCompleteBtn&&!markCompleteBtn.disabled){markCompleteBtn.click();}}createModal({title,message,buttons,autoClose}){const modal=document.createElement('div');modal.className='navigation-modal';const buttonsHTML=buttons.map(btn=>`<button class="btn ${btn.class}" data-action="${btn.text}">${btn.text}</button>`).join('');modal.innerHTML=`<div class="modal-overlay"><div class="modal-content"><h3 class="modal-title">${title}</h3><p class="modal-message">${message.replace(/\n/g,'<br>')}</p>${autoClose?`<p class="modal-countdown">Auto-navega√ß√£o em<span class="countdown">${Math.floor(autoClose/1000)}</span>s</p>`:''}<div class="modal-buttons">${buttonsHTML}</div></div></div>`;buttons.forEach(btn=>{const btnEl=modal.querySelector(`[data-action="${btn.text}"]`);if(btnEl){btnEl.addEventListener('click',btn.action);}});return modal;}closeModal(modal){modal.classList.remove('show');setTimeout(()=>{if(modal.parentNode){modal.parentNode.removeChild(modal);}},300);}closeNavigationModals(){const modals=document.querySelectorAll('.navigation-modal.show');modals.forEach(modal=>this.closeModal(modal));}showNotification(message,type='info'){if(typeof window.showNotification==='function'){window.showNotification(message,type);return;}const notification=document.createElement('div');notification.className=`navigation-notification notification-${type}`;notification.textContent=message;document.body.appendChild(notification);setTimeout(()=>notification.classList.add('show'),10);setTimeout(()=>{notification.classList.remove('show');setTimeout(()=>{if(notification.parentNode){notification.parentNode.removeChild(notification);}},300);},3000);}}document.addEventListener('DOMContentLoaded',()=>{new LessonNavigationManager();});window.LessonNavigationManager=LessonNavigationManager;